import React, { useState, useEffect } from 'react';
import { locate, laptop, time, alarmOutline, star } from "ionicons/icons";
import { Plugins } from "@capacitor/core";
import GoogleMapReact from 'google-map-react';
import { RefresherEventDetail } from '@ionic/core';
import { start } from 'repl';
import { IonPage, IonHeader, IonToolbar, IonTitle, IonButton, IonIcon, IonButtons, IonContent, IonLoading, useIonViewDidEnter } from '@ionic/react';
import { RouteComponentProps, useHistory } from 'react-router';
import { strict } from 'assert';
import shareValue from "../Models/share";
import { get } from 'https';


const { Geolocation } = Plugins;
const Tab3: React.FC<RouteComponentProps> = (props) => {
  const [showLoading, setShowLoading] = useState(false);
  const [positionReady, setPositionReady] = useState(false);
  const [currentPosition, setCurrentPosition] = useState({ lat: 0, lng: 0});
  const [startPosition, setStartPosition] = useState({ lat: 0, lng: 0});
  const [timeStatus, setTimeStatus] = useState({stime :0,ftime:0});
  const [totalDistance, setTotalDistance] = useState(0);
  const [watchID, setWatchID] = useState(0);

  useIonViewDidEnter(() => {
    startGetGPS();
  });

  // const startGetGPS = async () => {
  //   console.log('Getting...startGetGPS');
  //   setShowLoading(true);
  //   let position1 = await Geolocation.getCurrentPosition();
  //   setShowLoading(false);
  //   setPositionReady(true);
  //   setCurrentPosition({
  //     lat: position1.coords.latitude,
  //     lng: position1.coords.longitude,
  //     time : position1.timestamp
  //   });

  //   if (startReady == true) {

  //     setStartPosition({
  //       lat: position1.coords.latitude,
  //       lng: position1.coords.longitude

  //     })
  //     setStartReady(false);
  //   }
  //   // let lat = position1.coords.latitude;
  //   // let lng = position1.coords.longitude;
  //   console.log('Current startGetGPS', startPosition.lat, startPosition.lng);


  //}

  let position1: any;
  const startGetGPS = async () => {
    console.log('**** startGetGPS ***');
    setShowLoading(true)
    position1 = await Geolocation.getCurrentPosition({ maximumAge: 5000, timeout: 5000 }).then(success)
      // setPositionReady(true);
      // setStartPosition({
      //   lat: position.coords.latitude,
      //   lng: position.coords.longitude,
      //   time: position.timestamp
      // });
      // setCurrentPosition({
      //   lat: position.coords.latitude,
      //   lng: position.coords.longitude,
      //   time: position.timestamp
      // });
      // let lat = position.coords.latitude;
      // let lng = position.coords.longitude;
      // setShowLoading(false);

      .catch((error) => {
        //console.log('ไม่สามารถหาพิกัดได้ :', error.message);
        alert('Not found Position:' + error.message);
      });
  }

  let success = (position: any) => {
    setPositionReady(true);
    setWatchID(position1);
    setStartPosition({
      lat: position.coords.latitude,
      lng: position.coords.longitude
      });
    setCurrentPosition({
      lat: position.coords.latitude,
      lng: position.coords.longitude
    });

    setTimeStatus({
      stime: position.timestamp,
      ftime: position.timestamp
    });
    setTotalDistance(0);
    // let lat = position.coords.latitude;
    // let lng = position.coords.longitude;

    setShowLoading(false);
  }



  let watch: any;

  let startWatchGPS = () => {
    console.log('*** startWatchGPS **** :');
    watch = Geolocation.watchPosition({}, (watchsuccess));
    setWatchID(watch);
    //watch = Geolocation.watchPosition({}, (position, error) => {
    // {maximumAge:5000,timeout:5000}
    // setCurrentPosition({
    //   lat: position.coords.latitude,
    //   lng: position.coords.longitude,
    //   time: position.timestamp
    // });
    //sub_distance = sub_distance + findDistance(position_1, position_2);
    //setPositionReady(true);

    //})
  }
  let position_1 = { lat:0, lng:0 }
  let position_2 = { lat: 0, lng: 0 }
  let firstwatch = true;
  let firstcal = true;
  let sub_distance = 0;

  let watchsuccess = (position: any, error: any) => {
    if (firstwatch == true) {
      position_1.lat = startPosition.lat;
      position_2.lng = startPosition.lng;
      firstwatch = false;
    }
    else {
      position_1.lat = position_2.lat;
      position_1.lng = position_2.lng;
    }
    position_2.lat = position.coords.latitude;
    position_2.lng = position.coords.longitude;

    setCurrentPosition({
      lat: position.coords.latitude,
      lng: position.coords.longitude,
    });
    setTimeStatus({
      stime: timeStatus.stime,
      ftime: position.timestamp
    });

    console.log('position 1:', position_1.lat, position_2.lng);
    console.log('position_2:', position_2.lat, position_2.lng);
    console.log('first_dis', sub_distance);
    if (firstcal == false) {
      
      sub_distance = sub_distance + distanceInKmBetweenEarthCoordinates(position_1.lat,position_1.lng, position_2.lat,position_2.lng);
    }
    setTotalDistance(sub_distance);
    
    
    //console.log('distance', sub_distance);
    //setTotalDistance(sub_distance);
    setPositionReady(true);
    firstcal = false;
  }





  //let findDistance = (position1: any, position2: any) => {
  let findDistance = (lat1: any, lng1: any, lat2: any, lng2: any) => {
    let R = 6373; // km
    // let lat1 = position1.lat;
    // let lng1 = position1.lng;
    // let lat2 = position2.lat;
    // let lng2 = position2.lng;
    let dLat = toRad((lat2 - lat1));
    let dLng = toRad((lng2 - lng1));
    let a = Math.sin(dLat / 2) * Math.sin(dLat / 2) +
      toRad(Math.cos(lat1)) * toRad(Math.cos(lat2)) *
      Math.sin(dLng / 2) * Math.sin(dLng / 2);
    let c = 2 * Math.atan2(Math.sqrt(a), Math.sqrt(1 - a));
    let d = R * c;
    function toRad(Value: any) {
      /** Converts numeric degrees to radians */
      return Value * Math.PI / 180;
    }
    /* ทศนิยม 2 ตำแหน่ง */
    let d_round_to_2nd = Number(d.toFixed(2));
    console.log('d_round :', d_round_to_2nd);
    return d_round_to_2nd;
    //return d;
  };

  let degreesToRadians = (degrees:any)=> {
    return degrees * Math.PI / 180;
  }
  
  let distanceInKmBetweenEarthCoordinates = (lat1: any, lng1: any, lat2: any, lng2: any) => {
    var earthRadiusKm = 6371;
    console.log('lat1:',lat1,'lng1:',lng1,'lat2:',lat2,'lng2:',lng2);
    var dLat = degreesToRadians(lat2-lat1);
    var dLon = degreesToRadians(lng2-lng1);
  
    lat1 = degreesToRadians(lat1);
    lat2 = degreesToRadians(lat2);
    console.log('lat1 :',lat1);
    console.log('lat2 :',lat2);
  
    var a = Math.sin(dLat/2) * Math.sin(dLat/2) +
            Math.sin(dLon/2) * Math.sin(dLon/2) * Math.cos(lat1) * Math.cos(lat2); 
    console.log('var a:',a);
    var c = 2 * Math.atan2(Math.sqrt(a), Math.sqrt(1-a)); 
    console.log('var c:',c);
    console.log('cal :',earthRadiusKm * c);
    return earthRadiusKm * c;
  }



  let stopWatchGPS = (watchid: any) => {
    alert('StopWatch id : ' + watchid);
    let clearwatch = Geolocation.clearWatch(watchid);
  }



  /* เพิ่มเลขศูนย์หน้า วัน เดือน เวลา ที่เป็นเลขหลักเดียว */
  let addZero = (i: any) => {
    if (i < '10') {
      i = "0" + i;
    }
    return i;
  }

  let getDatetime = (time: any) => {
    let myDate = new Date(time);
    let d = addZero(myDate.getDate());
    let m = addZero((myDate.getMonth()) + 1);
    let y = myDate.getFullYear();
    let h = addZero(myDate.getHours());
    let min = addZero(myDate.getMinutes());
    let sec = addZero(myDate.getSeconds());
    let times = d + '-' + m + '-' + y + ' ' + h + ':' + min + ':' + sec
    return times;
  };

  let durationtime = (timestart: any, timestop: any) => {
    let time = timestop - timestart;
    let mystartDate = new Date(timestart);
    let mystopDate = new Date(timestop);
    let myDate = new Date(time);
    let h = addZero(myDate.getHours()) - 7;
    let min = addZero(myDate.getMinutes());
    let sec = addZero(myDate.getSeconds());
    let times;
    if (h == 0) {
      times = min + ':' + sec
    } else {
      times = h + ':' + min + ':' + sec
    }
    return times;
  };

  let calduration = () => {
    let x = durationtime(1453467965439, 1585492636444);
    console.log(x + " น.")
  }

  let calories = (distance: any, weight: any) => {

    let cal = (weight * distance * 1.036).toFixed(2)
    console.log(cal + " Kcal")
    return cal;

  };
  let calCalories = () => {
    let x = calories(308, 78);
    console.log(x + " Kcal.")
  }

  let startTextJSX;

  if (positionReady == true) {
    startTextJSX = <p>{currentPosition.lat},{currentPosition.lng}</p>
  }
  else {
    startTextJSX = <p>กดปุ่มบนขวาเพื่อระบุพิกัดของเครื่อง</p>
  }
  let stopTextJSX;
  let distanceTextJSX;
  let start_time = getDatetime(timeStatus.stime);
  let current_time = getDatetime(timeStatus.ftime);
  let duration = durationtime(timeStatus.stime,timeStatus.ftime);

  if (positionReady == true) {
    stopTextJSX = <p>
      SLAT = {startPosition.lat} <br></br>
      LNG = {startPosition.lng} <br></br>
      Time = {start_time} <br></br>
      <br></br>
      CLAT = {currentPosition.lat} <br></br>
      LNG = {currentPosition.lng} <br></br>
      Time = {current_time}<br></br>
      WatchID = {watchID}
      <br></br>
      Dis = {totalDistance}<br></br>
      Duration = {duration}
    </p>
  } else {
    stopTextJSX = <p>กดปุ่มบนขวาเพื่อระบุพิกัดของเครื่อง</p>
  }

  return (
    <IonPage>
      <IonHeader translucent={true}>
        <IonToolbar>
          <IonButton slot="start" color="primary" shape="round" size="large" onClick={() => { startWatchGPS() }} >WatchGPS</IonButton>
          <IonButton slot="end" color="secondary" shape="round" size="large" onClick={() => { stopWatchGPS(watchID) }}>StopGPS</IonButton>
        </IonToolbar>
      </IonHeader>
      <IonContent>
        <body >
          <div>
            {stopTextJSX}
          </div>
        </body>
        <IonLoading isOpen={showLoading} message="กำลังขอพิกัด" />
      </IonContent>
    </IonPage>
  );
};
export default Tab3;
