import React, { useState, useEffect } from 'react';
import { locate, laptop, time, alarmOutline } from "ionicons/icons";
import { Plugins } from "@capacitor/core";
import GoogleMapReact from 'google-map-react';
import { RefresherEventDetail } from '@ionic/core';
import { start } from 'repl';
import { IonPage, IonHeader, IonToolbar, IonTitle, IonButton, IonIcon, IonButtons, IonContent, IonLoading, useIonViewDidEnter } from '@ionic/react';
import { RouteComponentProps, useHistory } from 'react-router';
import { strict } from 'assert';
import shareValue from "../Models/share";


const { Geolocation } = Plugins;
const Tab3: React.FC<RouteComponentProps> = (props) => {
  const [showLoading, setShowLoading] = useState(false);
  const [positionReady, setPositionReady] = useState(false);
  const [currentPosition, setCurrentPosition] = useState({ lat: 0, lng: 0 });
  const [startPosition, setStartPosition] = useState({ lat: 0, lng: 0 });
  const [startReady, setStartReady] = useState(true);
  const [startStatus, setStartStatus] = useState({ time: 0 });
  const [totalDistance, setTotalDistance] = useState(0);






  useIonViewDidEnter(() => {
    //getDistance();

  });



  const startGetGPS = async () => {
    console.log('Getting...startGetGPS');
    setShowLoading(true);
    let position1 = await Geolocation.getCurrentPosition();
    setShowLoading(false);
    setPositionReady(true);
    setCurrentPosition({
      lat: position1.coords.latitude,
      lng: position1.coords.longitude
    });

    if (startReady == true) {
      setStartStatus({
        time: position1.timestamp
      })
      setStartPosition({
        lat: position1.coords.latitude,
        lng: position1.coords.longitude
        
      })
      setStartReady(false);
    }
    // let lat = position1.coords.latitude;
    // let lng = position1.coords.longitude;
    console.log('Current startGetGPS', startPosition.lat, startPosition.lng);


  }
  
  
  

  const getDistance = async () => {
    let position_1 = ({lat:0,lng:0});  // จุดเริ่มต้นจากการ GetGPS from Device
    console.log('**** getDistance ***');
    let position1 = await Geolocation.getCurrentPosition().then((position) => {

      setPositionReady(true);
      setCurrentPosition({
        lat: position.coords.latitude,
        lng: position.coords.longitude
      });
      let lat = position.coords.latitude;
      let lng = position.coords.longitude;
      
      // let datetime = getDatetime(position1.timestamp);
      alert("lat : " + lat + " lng : " + lng);
      position_1 = ({
        lat: position.coords.latitude,
        lng: position.coords.longitude
      });
    })
      .catch((error) => {
        //console.log('ไม่สามารถหาพิกัดได้ :', error.message);
        alert('ไม่สามารถหาพิกัดได้ :'+ error.message);
      });
      return (position_1);
  }

  let findDistance = (position1:any,position2:any) => {
    let R = 6373; // km

    let lat1 = position1.lat;
    let lng1 = position1.lng;
    let lat2 = position2.lat;
    let lng2 = position2.lng;

    let dLat = toRad((lat2 - lat1));
    let dLng = toRad((lng2 - lng1));
    let a = Math.sin(dLat / 2) * Math.sin(dLat / 2) +
      toRad(Math.cos(lat1)) * toRad(Math.cos(lat2)) *
      Math.sin(dLng / 2) * Math.sin(dLng / 2);
    let c = 2 * Math.atan2(Math.sqrt(a), Math.sqrt(1 - a));
    let d = R * c;
    function toRad(Value: any) {
      /** Converts numeric degrees to radians */
      return Value * Math.PI / 180;
    }
    /* ทศนิยม 2 ตำแหน่ง */
    let d_round_to_2nd = Number(d.toFixed(2));
    //return d_round_to_2nd;
    return d;
  };

  let stopWatchGPS = () => {
    Geolocation.clearWatch({id:watch1});
    
  }
  let totaldistance = 0;
  let watch1: any;
  let count = 0;
  let sub_distance = 0;
  
  let startWatchGPS = () => {
    let position_1:any;  // จุดเริ่มต้นจากการ GetGPS from Device
    position_1 = getDistance();
    console.log('Position : ',position_1.lat,position_1.lng)
    
    let position_2 =({lat:0,lng:0});  // จุดถัดมาจากการ GetGPS from Device
    
    
    count = count + 1;
    console.log('*** startWatchGPS **** :',count);
    let countwatchpostion = 0;
    watch1 = Geolocation.watchPosition({}, (position, error) => {
    // {maximumAge:5000,timeout:5000}
    countwatchpostion = countwatchpostion+1;
    console.log('WatchID :',watch1);
    console.log('*** New Position ***', position,' CountWatchPostion :',countwatchpostion);
    
    position_2 = ({
      lat: position.coords.latitude,
      lng: position.coords.longitude
    });
    console.log('Position : ',position_1.lat,position_1.lng,position_2.lat,position_2.lng)
    sub_distance = sub_distance + findDistance(position_1,position_2);
    console.log('Sub Distance : ',sub_distance);
    position_1 = position_2;
      
    })
    
    console.log("Total Distacne :",sub_distance);

  }
  




  

  
  
  /* เพิ่มเลขศูนย์หน้า วัน เดือน เวลา ที่เป็นเลขหลักเดียว */
  let addZero = (i: any) => {
    if (i < '10') {
      i = "0" + i;
    }
    return i;
  }

  let getDatetime = (time: any) => {

    let myDate = new Date(time);
    let d = addZero(myDate.getDate());
    let m = addZero((myDate.getMonth()) + 1);
    let y = myDate.getFullYear();
    let h = addZero(myDate.getHours());
    let min = addZero(myDate.getMinutes());
    let sec = addZero(myDate.getSeconds());
    let times = d + '-' + m + '-' + y + ' ' + h + ':' + min + ':' + sec
    //console.log('DateTime', times);
    return times;
  };


  let durationtime = (timestart: any, timestop: any) => {
    let time = timestop - timestart;
    let mystartDate = new Date(timestart);
    let mystopDate = new Date(timestop);
    let myDate = new Date(time);
    let h = addZero(myDate.getHours()) - 7;
    let min = addZero(myDate.getMinutes());
    let sec = addZero(myDate.getSeconds());
    let times;
    if (h == 0) {
      times = min + ':' + sec
    } else {
      times = h + ':' + min + ':' + sec
    }

    // console.log('Time Start :', mystartDate);
    // console.log('Time Stop :', mystopDate);
    // console.log('Diff Time :', h, ':', min, ':', sec);
    // console.log(times)
    return times;
  };

  let calduration = () => {
    let x = durationtime(1453467965439, 1585492636444);
    console.log(x + " น.")
  }

  let calories = (distance: any, weight: any) => {

    let cal = (weight * distance * 1.036).toFixed(2)
    console.log(cal + " Kcal")
    return cal;

  };
  let calCalories = () => {
    let x = calories(308, 78);
    console.log(x + " Kcal.")
  }

  let startTextJSX;

  if (positionReady == true) {
    startTextJSX = <p>{currentPosition.lat},{currentPosition.lng}</p>
  }
  else {
    startTextJSX = <p>กดปุ่มบนขวาเพื่อระบุพิกัดของเครื่อง</p>
  }
  let stopTextJSX;
  let distanceTextJSX;
  // let duration = durationtime(startStatus.time, currentPosition.time);
  

  if (positionReady == true) {


    // stopTextJSX = <p>LastPosition = {startPosition.lat}<br></br>
    //                 latitude = {startPosition.lng}<br></br>
    //                 Time = {starttime}<br></br>
    //                 CurrentPosition = {currentPosition.lat}<br></br>
    //                 latitude = {currentPosition.lng}<br></br>
    //                 timestamp = {stoptime}<br></br>
    //                 duration = {duration}</p>

    // distanceTextJSX = <p>totalDistance = {distance}</p>
  } else {
    stopTextJSX = <p>กดปุ่มบนขวาเพื่อระบุพิกัดของเครื่อง</p>
  }

  return (
    <IonPage>
      <IonHeader translucent={true}>
        <IonToolbar>
          <IonTitle>Easy Running#1</IonTitle>
          <IonButtons slot="end">
            <IonButton expand="full" color="primary" shape="round" slot="start" size="large" onClick={startWatchGPS} >WatchGPS</IonButton>
            <IonButton expand="block" color="secondary" shape="round" slot="start" size="large" onClick={stopWatchGPS}>StopGPS</IonButton>
          </IonButtons>
        </IonToolbar>
      </IonHeader>
      <IonContent>
        <body >
          <div style={{ height: '90vh', width: '100%' }}>

            //{stopTextJSX}
            

          </div>
        </body>
        <IonLoading isOpen={showLoading} message="กำลังขอพิกัด" />
      </IonContent>
    </IonPage>
  );
};
export default Tab3;
