import React, { useState, useEffect } from 'react';
import { locate, laptop, time, alarmOutline, star } from "ionicons/icons";
import { Plugins } from "@capacitor/core";
import GoogleMapReact from 'google-map-react';
import { RefresherEventDetail } from '@ionic/core';
import { start } from 'repl';
import { IonPage, IonHeader, IonToolbar, IonTitle, IonButton, IonIcon, IonButtons, IonContent, IonLoading, useIonViewDidEnter } from '@ionic/react';
import { RouteComponentProps, useHistory } from 'react-router';
import { strict } from 'assert';
import shareValue from "../Models/share";
import { get } from 'https';


const { Geolocation } = Plugins;
const Tab3: React.FC<RouteComponentProps> = (props) => {
  const [showLoading, setShowLoading] = useState(false);
  const [positionReady, setPositionReady] = useState(false);
  const [currentPosition, setCurrentPosition] = useState({ lat: 0, lng: 0 });
  const [startPosition, setStartPosition] = useState({ lat: 0, lng: 0 ,time: 0});
  const [timeStatus, setTimeStatus] = useState({ stime: 0, ftime: 0 });
  const [totalDistance, setTotalDistance] = useState(0);
  const [watchID, setWatchID] = useState(0);
  

  useIonViewDidEnter(() => {
    
  });

  useEffect(()=>{
    let counter:any;
    let timeout = setTimeout(() => {
      counter = counter + 1;
      startGetGPS(); 
      
    }, 10000);
    console.log('Counter : ',counter)

  });


  let position1: any;
  const startGetGPS = async () => {
    console.log('**** startGetGPS ***');
    setShowLoading(true)
    position1 = await Geolocation.getCurrentPosition({maximumAge: 2000, timeout: 2000}).then(success)
      .catch((error) => {
        alert('Not found Position:' + error.message);
      });
  }

  let success = (position: any) => {
    setPositionReady(true);
    setWatchID(position1);
    setStartPosition({
      lat: position.coords.latitude,
      lng: position.coords.longitude,
      time: position.timestamp
    });
    setCurrentPosition({
      lat: position.coords.latitude,
      lng: position.coords.longitude
    });
    
    setTotalDistance(0);
    setShowLoading(false);
    
  }
  
let watch: any;

  let startWatchGPS = () => {
    console.log('*** startWatchGPS **** :');
    watch = Geolocation.watchPosition({}, (watchsuccess));
    setWatchID(watch);
  }
  let position_1 = { lat: 0, lng: 0 }
  let position_2 = { lat: 0, lng: 0 }
  let firstwatch = true;
  let firstcal = true;
  let sub_distance = 0;

  let watchsuccess = (position: any, error: any) => {
    if (firstwatch == true) {
      position_1.lat = startPosition.lat;
      position_2.lng = startPosition.lng;
      firstwatch = false;
    }
    else {
      position_1.lat = position_2.lat;
      position_1.lng = position_2.lng;
    }
    position_2.lat = position.coords.latitude;
    position_2.lng = position.coords.longitude;

    setCurrentPosition({
      lat: position.coords.latitude,
      lng: position.coords.longitude,
    });
    // setTimeStatus({
    //   stime: startPosition.time,
    //   ftime: position.timestamp
    // });
    console.log("firstcal :",firstcal);
    if (firstcal == false) {
      sub_distance = sub_distance + distanceInKmBetweenEarthCoordinates(position_1, position_2);
    }
    setTotalDistance(sub_distance);
    setPositionReady(true);
    firstcal = false;
  }

  let degreesToRadians = (degrees: any) => {
    return degrees * Math.PI / 180;
  }

  let distanceInKmBetweenEarthCoordinates = (position1: any, position2: any) => {
    var earthRadiusKm = 6371;
    let lat1 = position1.lat;
    let lng1 = position1.lng;
    let lat2 = position2.lat;
    let lng2 = position2.lng;
    var dLat = degreesToRadians(lat2 - lat1);
    var dLon = degreesToRadians(lng2 - lng1);

    lat1 = degreesToRadians(lat1);
    lat2 = degreesToRadians(lat2);
    var a = Math.sin(dLat / 2) * Math.sin(dLat / 2) +
      Math.sin(dLon / 2) * Math.sin(dLon / 2) * Math.cos(lat1) * Math.cos(lat2);
    var c = 2 * Math.atan2(Math.sqrt(a), Math.sqrt(1 - a));
    let distancekm = earthRadiusKm * c;
    let d_round_to_2nd = Number(distancekm.toFixed(2));

    return d_round_to_2nd;
  }



  let stopWatchGPS = (watchid: any) => {
    console.log('watch id :',watchid);
    let clearwatch = Geolocation.clearWatch(watchid);
  }



  /* เพิ่มเลขศูนย์หน้า วัน เดือน เวลา ที่เป็นเลขหลักเดียว */
  let addZero = (i: any) => {
    if (i < '10') {
      i = "0" + i;
    }
    return i;
  }

  let getDatetime = (time: any) => {
    let myDate = new Date(time);
    let d = addZero(myDate.getDate());
    let m = addZero((myDate.getMonth()) + 1);
    let y = myDate.getFullYear();
    let h = addZero(myDate.getHours());
    let min = addZero(myDate.getMinutes());
    let sec = addZero(myDate.getSeconds());
    let times = d + '-' + m + '-' + y + ' ' + h + ':' + min + ':' + sec
    return times;
  };

  let durationtime = (timestart: any, timestop: any) => {
    let time = timestop - timestart;
    let mystartDate = new Date(timestart);
    let mystopDate = new Date(timestop);
    let myDate = new Date(time);
    let h = addZero(myDate.getHours()) - 7;
    let min = addZero(myDate.getMinutes());
    let sec = addZero(myDate.getSeconds());
    let times;
    if (h == 0) {
      times = min + ':' + sec
    } else {
      times = h + ':' + min + ':' + sec
    }
    return times;
  };

  let calduration = () => {
    let x = durationtime(1453467965439, 1585492636444);
    console.log(x + " น.")
  }

  let calories = (distance: any, weight: any) => {

    let cal = (weight * distance * 1.036).toFixed(2)
    console.log(cal + " Kcal")
    return cal;

  };
  let calCalories = () => {
    let x = calories(308, 78);
    console.log(x + " Kcal.")
  }

  
  let stopTextJSX;
  let distanceTextJSX;
  let start_time = getDatetime(timeStatus.stime);
  let current_time = getDatetime(timeStatus.ftime);
  let duration = durationtime(timeStatus.stime, timeStatus.ftime);

  if (positionReady == true) {
    stopTextJSX = <p>
      SLAT = {Number(startPosition.lat.toFixed(2))} <br></br>
      LNG = {Number(startPosition.lng.toFixed(2))} <br></br>
      Time = {start_time} <br></br>
      <br></br>
      CLAT = {Number(currentPosition.lat.toFixed(2))} <br></br>
      LNG = {Number(currentPosition.lng.toFixed(2))} <br></br>
      Time = {current_time}<br></br>
      <br></br>
      WatchGPS_ID = {watchID}<br></br>
      <br></br>
      Dis = {totalDistance}<br></br>
      Duration = {duration}
    </p>
  } else {
    stopTextJSX = <p>กดปุ่ม StarGetGPS เพื่อระบุพิกัดของเครื่อง</p>
  }

  return (
    <IonPage>
      <IonHeader translucent={true}>
        <IonToolbar>
          <IonButton slot="start" color="success" shape="round" size="large" onClick={() => { 
          setTimeStatus({
            stime : new Date().getTime(),
            ftime : new Date().getTime()
          });
          //startWatchGPS()
          }} >Start</IonButton>
          <IonButton slot="end" color="danger" shape="round" size="large" onClick={() => { 
          setTimeStatus({
            stime : timeStatus.stime,
            ftime : new Date().getTime()
          });
          stopWatchGPS(watchID)
          }}>Stop</IonButton>
        </IonToolbar>
      </IonHeader>
      <IonContent>
        <body >
          <div>
            {stopTextJSX}
          </div>
        </body>
        <IonLoading isOpen={showLoading} message="กำลังขอพิกัด" />
      </IonContent>
    </IonPage>
  );
};
export default Tab3;
