  //}

  let position1:any;
  const startGetGPS = async () => {
    console.log('**** startGetGPS ***');
    setShowLoading(true)
    let position1 = await Geolocation.getCurrentPosition({ maximumAge: 5000, timeout: 5000 }).then(success)
