import React, { useState } from 'react';
import { IonContent, IonHeader, IonPage, IonTitle, IonToolbar, IonAvatar, IonItem, IonLabel, IonIcon, IonInput, IonFooter, IonButton, IonAlert } from '@ionic/react';
import ExploreContainer from '../components/ExploreContainer';
import './Tab2.css';
import { timer } from 'ionicons/icons';
import { postrun } from '../services/postAPI';
import { RouteComponentProps } from 'react-router';

const Tab2: React.FC<RouteComponentProps> = (props) => {

  const [showAlert, setShowAlert] = useState(false);

  let user_id = "";
  let start_time = "";
  let stop_time = "";
  let duration = "";
  let distance = "";

  let onSave = async () => {
    console.log('ส่ง request บันทึกการวิ่ง');
    let result = await postrun(user_id, start_time, stop_time, duration, distance);
    if (result?.data.success) {
      props.history.goBack();
    }
  }

  return (
    <IonPage>
      <IonHeader>
        <IonToolbar>
          <h1>
            <IonTitle size="large" class="ion-text-center">ส่งผลการวิ่ง</IonTitle>
          </h1>
        </IonToolbar>
      </IonHeader>
      <IonContent>
        <IonAvatar className="avatar">
          <img src="/assets/icon/man.svg" />
        </IonAvatar>
        <div>
          <IonItem lines="none">
            <IonLabel>เริ่มวิ่งเวลา
            <IonIcon icon={timer} color="primary" size="large" slot="icon-only"></IonIcon>
            </IonLabel>
            <IonInput color="primary"></IonInput>
          </IonItem>
          <IonItem lines="none">
            <IonLabel>เวลาสิ้นสุดการวิ่ง</IonLabel>
            <IonInput color="primary"></IonInput>
          </IonItem>
          <IonItem lines="none">
            <IonLabel>รวมเวลาวิ่ง</IonLabel>
            <IonInput color="primary"></IonInput>
          </IonItem>
          <IonItem lines="none">
            <IonLabel>ท่านวิ่งได้ระยะทาง</IonLabel>
            <IonInput color="primary" ></IonInput>
            <IonLabel>กิโลเมตร</IonLabel>
          </IonItem>
          <IonItem>


          </IonItem>
        </div>
      </IonContent>
      <IonFooter className="ion-padding">

        <IonButton onClick={() => setShowAlert(true)} expand="full" shape="round" type="submit">บันทึก</IonButton>
        <IonButton expand="full" shape="round">ยกเลิก</IonButton>
        <IonAlert
          isOpen={showAlert}
          onDidDismiss={() => setShowAlert(false)}
          header={'ส่งผลการวิ่ง'}
          subHeader={'บันทึกเรียบร้อยแล้ว'}
          message={'ขอบคุณครับ'}
          buttons={['ตกลง']}
        />

      </IonFooter>
      <br>
      </br>
    </IonPage>
  );
};

export default Tab2;
