import React, { useState } from 'react';
import { IonContent, IonHeader, IonPage, IonTitle, IonToolbar, IonAvatar, IonItem, IonLabel, IonIcon, IonInput, IonFooter, IonButton, IonAlert, IonDatetime } from '@ionic/react';
import ExploreContainer from '../components/ExploreContainer';
import './Tab2.css';
import { timer } from 'ionicons/icons';
import { addActivity } from '../services/ActivityAPI';
import { RouteComponentProps } from 'react-router';

const Tab2: React.FC<RouteComponentProps> = (props) => {

  const [showAlert, setShowAlert] = useState(false);

  let username = "ple2";
  let walkRun = "30";
  let startTime = "2020-03-30 17:30:07";
  let stopTime = "2020-03-30 18:00:08";
  let distance = "5";

  let onSave = async () => {
    console.log('onsave  : ส่ง request บันทึกการวิ่ง');

    let result = await addActivity(username, walkRun, startTime, stopTime, distance);
     if (result?.data.success) {
      setShowAlert(true);
      props.history.goBack();
    }
  }

  return (
    <IonPage>
      <IonHeader>
        <IonToolbar>

            <IonTitle size = "large" class="ion-text-center">ส่งผลการวิ่ง</IonTitle>

        </IonToolbar>
      </IonHeader>
      <IonContent>
        <IonAvatar className="avatar">
          <img src="/assets/icon/man.svg" />
        </IonAvatar>

          <IonItem lines="none">
            <IonLabel>เริ่มวิ่งเวลา</IonLabel>
            <IonInput color="primary" placeholder={startTime}></IonInput>

          </IonItem>
          <IonItem lines="none">
            <IonLabel>สิ้นสุดการวิ่ง</IonLabel>
            <IonInput color="primary" placeholder={stopTime}></IonInput>
          </IonItem>
          <IonItem lines="none">
            <IonLabel>รวมเวลาวิ่ง</IonLabel>
            <IonInput color="primary" placeholder={walkRun} ></IonInput>
          </IonItem>
          <IonItem lines="none">
            <IonLabel>ท่านวิ่งได้ระยะทาง</IonLabel>
            <IonInput color="primary" placeholder={distance} ></IonInput>
            <IonLabel>กิโลเมตร</IonLabel>
          </IonItem>

      </IonContent>
      <IonFooter className="ion-padding">

        <IonButton onClick={onSave} expand="full" shape="round" type="submit">บันทึก</IonButton>
        <IonButton onClick={()=>props.history.goBack()} expand="full" shape="round">ยกเลิก</IonButton>
        <IonAlert
          isOpen={showAlert}
          onDidDismiss={() => setShowAlert(false)}
          header={'ส่งผลการวิ่ง'}
          subHeader={'บันทึกเรียบร้อยแล้ว'}
          message={'ขอบคุณครับ'}
          buttons={['ตกลง']}
        />

      </IonFooter>
      <br>
      </br>
    </IonPage>
  );
};

export default Tab2;
