import React, { useState } from 'react';
import { locate, laptop, time } from "ionicons/icons";
import { Plugins } from "@capacitor/core";
import GoogleMapReact from 'google-map-react';
import { RefresherEventDetail } from '@ionic/core';
import { start } from 'repl';
import { IonPage, IonHeader, IonToolbar, IonTitle, IonButton, IonIcon, IonButtons, IonContent, IonLoading, useIonViewDidEnter } from '@ionic/react';
import { RouteComponentProps } from 'react-router';

const { Geolocation } = Plugins;
const Tab4: React.FC<RouteComponentProps> = (props) => {
  const [showLoading, setShowLoading] = useState(false);
  const [positionReady, setPositionReady] = useState(false);
  const [currentPosition, setCurrentPosition] = useState({ lat: 0, lng: 0, acc: 0, time: 0 });
  let onMapLoad = (map: any, mapsAPI: any) => {
    let directionsService = new mapsAPI.DirectionsService();
    let directionsRenderer = new mapsAPI.DirectionsRenderer();
    directionsRenderer.setMap(map)
    // let start = { lat: 9.1106153, lng: 99.3018452 };
    let start = { lat : currentPosition.lat,lng : currentPosition.lng}
    // let end = { lat: 6.9506242, lng: 100.41277989999999 };
    let end = { lat : currentPosition.lat,lng : currentPosition.lng}
    let request = {
      origin: start,
      destination: end,
       travelMode: 'WALKING'
    };
    directionsService.route(request, function (result: any, status: any) {
      if (status == 'OK') {
        directionsRenderer.setDirections(result);
        let route = result.routes[0];
        for (let i = 0; i < route.legs.length; i++) {
          console.log('directionService',route.legs[i].distance.text)
        }
      }
    });
    let trafficLayer = new mapsAPI.TrafficLayer();
  }
  
  useIonViewDidEnter(()=>{
     startGetGPS();
    // startDistance();
  });

  const getDirection = async () => {
    console.log('Getting geolocation....');
    setShowLoading(true);
    let position2 = await Geolocation.getCurrentPosition();
    setShowLoading(false);
    setPositionReady(true);
  }
  const startGetGPS = async () => {
    console.log('Getting geolocation....startGetGPS');
    setShowLoading(true);
    let position1 = await Geolocation.getCurrentPosition();
    setShowLoading(false);
    setPositionReady(true);
    setCurrentPosition({
      lat: position1.coords.latitude,
      lng: position1.coords.longitude,
      acc: position1.coords.accuracy,
      time: position1.timestamp
    });
    let lat = position1.coords.latitude;
    let lng = position1.coords.longitude;
    

    console.log('Current startGetGPS', lat, lng);

  }
  const startDistance = async () => {
    console.log('Getting geolocation....startDistance');
    setShowLoading(true);
    let position1 = await Geolocation.getCurrentPosition();
    setShowLoading(false);
    setPositionReady(true);
    setCurrentPosition({
      lat: position1.coords.latitude,
      lng: position1.coords.longitude,
      acc: position1.coords.accuracy,
      time: position1.timestamp
    });
    let lat = position1.coords.latitude;
    let lng = position1.coords.longitude;
    let t = position1.timestamp;
    // let datetime = new Date(t);
    console.log('Current startDistance', lat, lng, t);
    // console.log('Current startDistance', lat, lng, 'time :',datetime);
    console.log('Current startDistance', position1);
  }
  const getDistance = async () => {
    console.log('Getting geolocation....getDistance');
    setShowLoading(true);
    let position1 = await Geolocation.getCurrentPosition();
    setShowLoading(false);
    setPositionReady(true);
    setCurrentPosition({
      lat: position1.coords.latitude,
      lng: position1.coords.longitude,
      acc: position1.coords.accuracy,
      time: position1.timestamp
    });
    let googleMap;
    if (positionReady) {
      googleMap = (
        <GoogleMapReact
          bootstrapURLKeys={{ key: 'AIzaSyATDm0MlJIxUXoIU56PIOk3aY26XXs6ul0' }}
          defaultCenter={currentPosition}
          defaultZoom={15}
          yesIWantToUseGoogleMapApiInternals
          onGoogleApiLoaded={({ map, maps }) => { onMapLoad(map, maps) }} >
        </GoogleMapReact>
      )
    }
  }
  let watch1;
  let startWatchGPS = () => {
    console.log('Watching geolocation...');
    setShowLoading(true);
    watch1 = Geolocation.watchPosition({}, (position, error) => {
      console.log('new position', position);
      setShowLoading(false);
      setPositionReady(true);
      setCurrentPosition({
        lat: position.coords.latitude,
        lng: position.coords.longitude,
        acc: position.coords.accuracy,
        time: position.timestamp
      })
    }
    );
  }
let startTextJSX;

  if (positionReady == true) {
    startTextJSX = <p>{currentPosition.lat},{currentPosition.lng},{currentPosition.time}</p>
  }
  else {
    startTextJSX = <p>กดปุ่มบนขวาเพื่อระบุพิกัดของเครื่อง</p>
  }
 let stopTextJSX ;
 
  if (positionReady == true) {
    // stopTextJSX =  <p>{currentPosition.lat} , {currentPosition.lng} ,{currentPosition.time}</p>;
    
    stopTextJSX = <p>stopTextJSX longtitude = {currentPosition.lat}<br></br>latitude = {currentPosition.lng}<br></br>timestamp = {currentPosition.time}</p>
  } else {
    stopTextJSX = <p>กดปุ่มบนขวาเพื่อระบุพิกัดของเครื่อง</p>
  }
    let googleMap;
  if (positionReady) {
    googleMap = (
      <GoogleMapReact
        bootstrapURLKeys={{ key: 'AIzaSyATDm0MlJIxUXoIU56PIOk3aY26XXs6ul0' }}
        defaultCenter={currentPosition}
        defaultZoom={15}
        yesIWantToUseGoogleMapApiInternals
        onGoogleApiLoaded={({ map, maps }) => { onMapLoad(map, maps) }} >
      </GoogleMapReact>
    )
  }
  return (
     <IonPage>
      <IonHeader translucent={true}>
         <IonToolbar>
          <IonTitle>Easy Running#1</IonTitle>
          <IonButtons slot = "end">
          <IonButton onClick={getDirection}>
               <IonIcon icon={locate} slot="icon-only"></IonIcon>
             </IonButton>
            <IonButton expand="full" color="primary" shape="round" slot="start" size="large" onClick={startDistance}>Start</IonButton>
            <IonButton expand="block" color="secondary" shape="round" slot="start" size="large" onClick={
              () => {
                getDistance()
                props.history.push('/Tab2')
                }
              }>
                Stop</IonButton>
           </IonButtons>
        </IonToolbar>
       </IonHeader>
       <IonContent>
         <body >
           <div style={{ height: '100vh', width: '100%' }}>
             {/* {startTextJSX} */}
             {stopTextJSX}
             {googleMap}
             {/* {positionTextJSX} */}
             {/* {googleMap}  */}
             {/* {DistanceTextJSX} */}
           </div>
         </body>
         <IonLoading isOpen={showLoading} message="กำลังขอพิกัด" />
       </IonContent>
     </IonPage>
  );
  };
export default Tab4;
