import React, { useState, useEffect } from 'react';
import { locate, laptop, time } from "ionicons/icons";
import { Plugins } from "@capacitor/core";
import GoogleMapReact from 'google-map-react';
import { RefresherEventDetail } from '@ionic/core';
import { start } from 'repl';
import { IonPage, IonHeader, IonToolbar, IonTitle, IonButton, IonIcon, IonButtons, IonContent, IonLoading, useIonViewDidEnter } from '@ionic/react';
import { RouteComponentProps, useHistory } from 'react-router';

const { Geolocation } = Plugins;
const Tab4: React.FC<RouteComponentProps> = (props) => {
  const [showLoading, setShowLoading] = useState(false);
  const [positionReady, setPositionReady] = useState(false);
  const [currentPosition, setCurrentPosition] = useState({ lat: 0, lng: 0, acc: 0, time: 0 });
  
  

  let onMapLoad = (map: any, mapsAPI: any) => {
    let directionsService = new mapsAPI.DirectionsService();
    let directionsRenderer = new mapsAPI.DirectionsRenderer();
    directionsRenderer.setMap(map)
    let start = { lat: 9.1106153, lng: 99.3018452 };
    
    let end = { lat: 6.9506242, lng: 100.41277989999999 };
    
    var waypts = [7.669402,100.022582]
    
    let request = {
      origin: end,
      destination: end,
      waypoints: [
      {
        location: "7.0451252, 100.1912893",
        stopover: true
      },
      {
        location: "7.003344, 100.326394",
        stopover: true
      }
    ],
      optimizeWaypoints: true,
      travelMode: 'WALKING'

    };

    directionsService.route(request, function (result: any, status: any) {

      if (status == 'OK') {
        directionsRenderer.setDirections(result);
        let route = result.routes[0];

        var totalDist = 0;

        for (let i = 0; i < route.legs.length; i++) {
          console.log('directionService', route.legs[i].distance.text)
          totalDist += route.legs[i].distance.value;
        }
        

        let dis1 = route.legs[route.legs.length - 1].distance.value / 1000; // distance : km.
        console.log(dis1 + ' km.')
        let weight = 78 // kg.
        let cal = weight * dis1 * 1.036
        console.log(cal.toFixed(2) + " Kcal")
      }
    });
    let trafficLayer = new mapsAPI.TrafficLayer();
  }

  useIonViewDidEnter(() => {
    //startGetGPS();
    // startDistance();
      //GetLatLng();
  });

  const getDirection = async () => {
    console.log('Getting geolocation....');
    setShowLoading(true);
    let position2 = await Geolocation.getCurrentPosition();
    setShowLoading(false);
    setPositionReady(true);
  }

  const startGetGPS = async () => {
    console.log('Getting geolocation....startGetGPS');
    setShowLoading(true);
    let position1 = await Geolocation.getCurrentPosition();
    setShowLoading(false);
    setPositionReady(true);
    
    setCurrentPosition({
      lat: position1.coords.latitude,
      lng: position1.coords.longitude,
      acc: position1.coords.accuracy,
      time: position1.timestamp
    });
    let lat = position1.coords.latitude;
    let lng = position1.coords.longitude;
    let datetime = new Date(position1.timestamp);

    console.log('Current startGetGPS', lat, lng);
    

  }
  const startDistance = async () => {
    console.log('Getting geolocation....startDistance');
    setShowLoading(true);
    let position1 = await Geolocation.getCurrentPosition();

    setShowLoading(false);
    setPositionReady(true);

    setCurrentPosition({
      lat: position1.coords.latitude,
      lng: position1.coords.longitude,
      acc: position1.coords.accuracy,
      time: position1.timestamp
    });

    let lat = position1.coords.latitude;
    let lng = position1.coords.longitude;
}
  const getDistance = async () => {
    console.log('Getting geolocation....getDistance');
    setShowLoading(true);
    let position1 = await Geolocation.getCurrentPosition();
    setShowLoading(false);
    setPositionReady(true);
    setCurrentPosition({
      lat: position1.coords.latitude,
      lng: position1.coords.longitude,
      acc: position1.coords.accuracy,
      time: position1.timestamp
    });
    let lat = position1.coords.latitude;
    let lng = position1.coords.longitude;
    console.log('Stop timestamp', new Date(currentPosition.time), lat, lng);


    let googleMap;
    if (positionReady) {
      googleMap = (
        <GoogleMapReact
          bootstrapURLKeys={{ key: 'AIzaSyATDm0MlJIxUXoIU56PIOk3aY26XXs6ul0' }}
          defaultCenter={currentPosition}
          defaultZoom={15}
          yesIWantToUseGoogleMapApiInternals
          onGoogleApiLoaded={({ map, maps }) => { onMapLoad(map, maps) }} >
        </GoogleMapReact>
      )
    }
  }
  let watch1;
  let startWatchGPS = () => {
    console.log('Watching geolocation...startWatchGPS');
    setShowLoading(true);
    watch1 = Geolocation.watchPosition({}, (position, error) => {
      console.log('new position', position);
      setShowLoading(false);
      setPositionReady(true);
      setCurrentPosition({
        lat: position.coords.latitude,
        lng: position.coords.longitude,
        acc: position.coords.accuracy,
        time: position.timestamp
      })
    }
    );
  }

  let GetLatLng = () => {
    let watch9
    console.log('Watching geolocation...fn.GetLatLng');
    setShowLoading(true);
    watch9 = Geolocation.watchPosition({}, (position9, error) => {
      console.log('new position', position9);
      setShowLoading(false);
      setPositionReady(true);
      setCurrentPosition({
        lat: position9.coords.latitude,
        lng: position9.coords.longitude,
        acc: position9.coords.accuracy,
        time: position9.timestamp
      });

      let lats = position9.coords.latitude;
      let lngs = position9.coords.longitude;
      let times = position9.timestamp;
      
      console.log('startPosition :',lats);
      console.log('currentPosition :',currentPosition.time);
    })
  }

  let findDistance = (lat1: any, lon1: any, lat2: any, lon2: any) => {
    var R = 6373; // km
    var dLat = toRad((lat2 - lat1));
    var dLon = toRad((lon2 - lon1));
    var a = Math.sin(dLat / 2) * Math.sin(dLat / 2) +
      toRad(Math.cos(lat1)) * toRad(Math.cos(lat2)) *
      Math.sin(dLon / 2) * Math.sin(dLon / 2);
    var c = 2 * Math.atan2(Math.sqrt(a), Math.sqrt(1 - a));
    var d = R * c;
    function toRad(Value: any) {
      /** Converts numeric degrees to radians */
      return Value * Math.PI / 180;
    }
    console.log("ระยะทางได้ผลลัพธ์ = " + d);
    return d;
  };

  let duration = (timestart: any, timestop: any) => {
    let time = timestop - timestart;
    let myDate = new Date(time);
    let d = myDate.getDate();
    let m = myDate.getMonth();
    let y = myDate.getFullYear();
    let h = myDate.getHours()
    let min = myDate.getMinutes()
    let sec = myDate.getSeconds()
    let times = h + ':' + min + ':' + sec
    console.log('Current', h, ':', min, ':', sec);
    console.log(times)
    return times;
  };

  let calduration = () => {
    let x = duration(1453467965439, 1585492636444);
    console.log(x + " น.")
  }

  let calories = (distance: any, weight: any) => {

    let cal = (weight * distance * 1.036).toFixed(2)
    console.log(cal + " Kcal")
    return cal;

  };
  let calCalories = () => {
    let x = calories(308, 78);
    console.log(x + " Kcal.")
  }

  let startTextJSX;

  if (positionReady == true) {
    startTextJSX = <p>{currentPosition.lat},{currentPosition.lng},{currentPosition.time}</p>
  }
  else {
    startTextJSX = <p>กดปุ่มบนขวาเพื่อระบุพิกัดของเครื่อง</p>
  }
  let stopTextJSX;


  if (positionReady == true) {


    stopTextJSX = <p>stopTextJSX longtitude = {currentPosition.lat}<br></br>latitude = {currentPosition.lng}<br></br>timestamp = {currentPosition.time}</p>
  } else {
    stopTextJSX = <p>กดปุ่มบนขวาเพื่อระบุพิกัดของเครื่อง</p>
  }
  let googleMap;
  if (positionReady) {
    googleMap = (
      <GoogleMapReact
        bootstrapURLKeys={{ key: 'AIzaSyATDm0MlJIxUXoIU56PIOk3aY26XXs6ul0' }}
        defaultCenter={currentPosition}
        defaultZoom={15}
        yesIWantToUseGoogleMapApiInternals
        onGoogleApiLoaded={({ map, maps }) => { onMapLoad(map, maps) }} >
      </GoogleMapReact>
    )
  }
  return (
    <IonPage>
      <IonHeader translucent={true}>
        <IonToolbar>
          <IonTitle>Easy Running#1</IonTitle>
          <IonButtons slot="end">
            <IonButton onClick={startWatchGPS}>
              <IonIcon icon={locate} slot="icon-only"></IonIcon>
            </IonButton>
            <IonButton expand="full" color="primary" shape="round" slot="start" size="large" onClick={GetLatLng}>Start</IonButton>
            <IonButton expand="block" color="secondary" shape="round" slot="start" size="large" onClick={getDirection}>Stop</IonButton>
          </IonButtons>
        </IonToolbar>
      </IonHeader>
      <IonContent>
        <body >
          <div style={{ height: '90vh', width: '100%' }}>
            {/* {startTextJSX} */}
            {stopTextJSX}
            {/*googleMap*/}
            {/* {positionTextJSX} */}
            {/* {googleMap}  */}
            {/* {DistanceTextJSX} */}
          </div>
        </body>
        <IonLoading isOpen={showLoading} message="กำลังขอพิกัด" />
      </IonContent>
    </IonPage>
  );
};
export default Tab4;
